import React, {useEffect} from "react";
import * as S from './cursor.styles'
import Cursor from "../../../utils/cursors";
import "./cursors.scss";
import {getMousePos} from "../../../utils/helpers";

const CursorAnimation = () => {

    useEffect(() => {
        const cursor = new Cursor(document.querySelector(".cursor"));
    }, [])
    return (
        <S.Cursor_Container>


                <svg className={"cursor"} viewBox="0 0 111 115" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" clip-rule="evenodd" d="M109.37 56.1979C110.778 74.2013 114.71 95.0372 101.19 107.039C87.2574 119.407 66.3138 114.707 48.1361 110.557C29.9732 106.411 9.05457 101.564 1.93343 84.3796C-4.96765 67.7267 8.15858 50.9771 16.9147 35.2168C24.7389 21.1339 32.4608 5.60335 48.0356 1.39939C64.1935 -2.96194 81.474 3.28228 93.9453 14.4247C105.601 24.8382 108.152 40.6323 109.37 56.1979Z" fill="#EB5757"/>
                </svg>
                <div className="cursor-inner">

                </div>

            <div className="s-cursor">

            </div>
        </S.Cursor_Container>
    );
};

export default CursorAnimation;
