import React, {useEffect} from 'react';
import * as S from './home_projects.styles'
import Text from "../common/Text";
import Project_Item from "./Project_Item";
import {Swiper , SwiperSlide} from "swiper/react";
import gsap from 'gsap';
import {ScrollTrigger} from "gsap/ScrollTrigger";


gsap.registerPlugin(ScrollTrigger);

const projects = [
  {
    "name": "Start Creation",
    "image": "/startcreation.png",
    "discipline": "UX/UI Website",
  },
  {
    "name": "Buddy Belgium",
    "image": "/buddybelgium.png",
    "discipline": "UX/UI Website",
  },
  {
    "name": "Cercle d'Histoire D'Auderghem",
    "image": "/chauderghem.png",
    "discipline": "UX/UI Website",
  },{
    "name": "Cercle d'Histoire D'Auderghem",
    "image": "/chauderghem.png",
    "discipline": "UX/UI Website",
  }
]


const Home_Projects = () => {

    useEffect(()=> {

        const cards = document.querySelectorAll('.cards');
        gsap.from(cards , {duration:1 , opacity:0 , stagger:0.2, scrollTrigger:{
            trigger: ".project-section",
                markers:true
            }})
    },[])
  return (
    <S.Home_Project_Container >
      <S.Home_Project_Title_Layer>
        <Text
          as={"h2"}
          fontSize={"170px"}
          fontWeight={600}
          lineHeight={"198%"}
          letterSpacing={"-0.02rem"}

          color={"#000000"}
        >
          Recent Projects
        </Text>
      </S.Home_Project_Title_Layer>
      <S.Home_Project_Wrapper>

        <S.Home_Project_List className={"project-section"}>
          <Swiper
            spaceBetween={37}
            slidesPerView={3}
            onSlideChange={() => console.log('slide change')}
            onSwiper={(swiper) => console.log(swiper)}
            centeredSlides={true}
            pagination={{"clickable": true}}
            breakpoints={{
              315:{
                width:343,
                slidesPerView:1,
                centeredSlides:false
              },
              480:{
                width:337,
                slidesPerView:1,
                centeredSlides:false
              },
              640: {
                width: 740,
                slidesPerView: 2,
                centeredSlides:true
              },
              768: {
                width: 1170,
                slidesPerView: 3,
                centeredSlides:true

              },1100: {
                width: 1100,
                slidesPerView: 3,
                centeredSlides:true
              },

            }}
          >
          {
            projects.map((project, i) => (
              <SwiperSlide
                key={i}

                    className={"project-card"}
              >
                <Project_Item
                  image={project.image}
                  discipline={project.discipline}
                  title={project.name}
                  index={`${i + 1}/${projects.length}`}
                  link={project.image}/>
              </SwiperSlide>
            ))
            }
          </Swiper>

        </S.Home_Project_List>
      </S.Home_Project_Wrapper>
    </S.Home_Project_Container>
  )
}

export default Home_Projects;
