import React, {useEffect} from 'react';
import * as S from "./project-header.styles"
import Text from "../common/Text";
import {Link} from 'react-router-dom';
import {Blob} from "react-blob";
import {Sketch} from "../../utils/header.skecth";





const Project_Header = ({color, title, expertise, technologies, site, image}) => {
    useEffect(() => {
        new Sketch(
            document.querySelector('.sketch-project'),
            "M397.09 6.2687C468.308 -3.14236 547.544 -4.11786 604.712 39.3094C661.512 82.4569 675.5 159.718 690.725 229.327C704.564 292.595 695.845 354.577 687.723 418.826C677.405 500.443 693.717 594.501 637.015 654.199C578.209 716.112 482.36 730.657 397.09 724.814C315.294 719.21 236.236 682.398 179.958 622.883C129.012 569.008 135.5 487.093 107.439 418.508C76.0016 341.672 -22.1091 275.829 6.00366 197.72C34.3354 119.003 150.668 126.197 225.902 89.367C284.301 60.7785 332.609 14.7895 397.09 6.2687Z",
            image

        )
    }, []);

    return (
        <S.Project_Header_Wrapper>
            <S.Project_Title_Wrapper>
                <Text
                    as={"h2"}
                    color={color}
                    fontSize={"80px"}
                    fontWeight={600}
                    lineHeight={"84.5%"}
                >{title}</Text>
                <div>
                    <Text
                        as={"h3"}
                        color={color}
                        fontSize={"12px"}
                        fontWeight={800}
                        lineHeight={"98.5%"}
                    >EXPERTISE</Text>
                    <Text>
                        {expertise}
                    </Text>
                </div>
                <div>
                    <Text
                        as={"h3"}
                        color={color}
                        fontSize={"12px"}
                        fontWeight={800}
                        lineHeight={"98.5%"}
                    >Platform</Text>
                    <Text>
                        {expertise}
                    </Text>
                </div>
                <div>
                    <Text
                        as={"h3"}
                        color={color}
                        fontSize={"12px"}
                        fontWeight={800}
                        lineHeight={"98.5%"}
                    >Technology</Text>
                    <Text>
                        {technologies}
                    </Text>
                </div>
                <div>
                    <Text
                        as={"h3"}
                        color={color}
                        fontSize={"12px"}
                        fontWeight={800}
                        lineHeight={"98.5%"}
                    >Link</Text>
                    <Text>
                        <Link to={site}>
                            <Text
                            textDecoration={"underline"}
                            fontSize={"16px"}
                            color={color}
                            >
                                See Project
                            </Text>
                        </Link>
                    </Text>
                </div>

            </S.Project_Title_Wrapper>
            <div className="sketch-project">

            </div>
            <canvas id="paper">

            </canvas>
        </S.Project_Header_Wrapper>

    )
}

export default Project_Header;
