import { gsap  } from "gsap";
import {CSSRulePlugin} from "gsap/CSSRulePlugin"
import { lerp, getMousePos, getSiblings } from "./helpers/index";



gsap.registerPlugin(CSSRulePlugin)
let mouse = { x: 0, y: 0 };
window.addEventListener("mousemove", (ev) => (mouse = getMousePos(ev)));
export default class Cursor {
    constructor(el) {
        this.Cursor = el;
        this.Cursor.style.opacity = 0;
        this.Item = document.querySelectorAll(".list-item");
        this.Projects = document.querySelectorAll(".chars");
        this.Link = document.querySelectorAll(".nav-main-link");
        this.Title = document.querySelectorAll("h2");
        this.Hero = document.querySelector(".hero-inner");
        this.bounds = this.Cursor.getBoundingClientRect();
        this.cursorConfigs = {
            x: { previous: 0, current: 0, amt: 0.07 },
            y: { previous: 0, current: 0, amt: 0.07}
        };
        this.onMouseMoveEv = () => {
            this.cursorConfigs.x.previous = this.cursorConfigs.x.current = mouse.x;
            this.cursorConfigs.y.previous = this.cursorConfigs.y.previous = mouse.y;
            gsap.to(this.Cursor, {
                duration: 1,
                ease: "Power3.easeOut",
                opacity: 1,
            });
            this.onScaleMouse()
            requestAnimationFrame(() => this.render());
            window.removeEventListener("mousemove", this.onMouseMoveEv);
        };
        window.addEventListener("mousemove", this.onMouseMoveEv);
    }
    onScaleMouse() {
        this.Item.forEach((link)=> {

            if(link.matches(':hover')) {
                this.Cursor.style.mixBlendMode = "unset"
                this.setImage(link)
                this.scaleAnimation(this.Cursor.children[0] , 0.8)
            }

            link.addEventListener("mouseenter" , ()=> {
                this.Cursor.style.mixBlendMode = "unset"
                this.setImage(link)

                this.scaleAnimation(this.Cursor.children[0] , 0.8);
            });
            link.addEventListener('mouseleave' , ()=> {
                this.scaleAnimation(this.Cursor.children[0] , 0);

            })
        })
        this.Link.forEach((link)=> {
            const element = CSSRulePlugin.getRule(`.cursor::before`)
            if(link.matches(':hover')) {
                this.scaleAnimation(element, 1.2)
            }

            link.addEventListener("mouseenter" , ()=> {
                this.scaleAnimation(element , 1.2);
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: 1.2
                    }
                })




            });
            link.addEventListener('mouseleave' , ()=> {
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: .2
                    }
                });

            })
        })
        this.Title.forEach((title) => {
            const element = CSSRulePlugin.getRule(`.cursor::before`)
            if(title.matches(':hover')) {
                this.scaleAnimation(element, 1.2)
            }

            title.addEventListener("mouseenter" , ()=> {
                this.scaleAnimation(element , 1.2);
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: 1.2
                    }
                })




            });
            title.addEventListener('mouseleave' , ()=> {
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: .2
                    }
                });
            })

        })
        this.Projects.forEach((title) => {
            const element = CSSRulePlugin.getRule(`.cursor::before`)
            if(title.matches(':hover')) {
                this.scaleAnimation(element, 1.2)
            }

            title.addEventListener("mouseenter" , ()=> {
                this.scaleAnimation(element , 1.2);
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: 1.2
                    }
                })





            });
            title.addEventListener('mouseleave' , ()=> {
                gsap.to(CSSRulePlugin.getRule('.cursor::before'), {
                    duration:.4,
                    cssRule:{
                        scale: .2
                    }
                });
            })

        })
    }

    scaleAnimation(el , amt) {
        gsap.to(el,{
            duration:0.6,
            scale:amt,
            ease: "Power3.easeOut"
        })
    }

    setImage(e) {
    /*    let src = e.getAttribute("data-img-src");
        let image = document.querySelector(`#${src}`);
        let siblings = getSiblings(image)
        if(image.id === src) {
            gsap.set(image , {zIndex: 4 , opacity:1});
            siblings.forEach((i)=> {
                gsap.set(i , {zIndex:1, opacity:0})
            });
        }*/

    }
    render() {
        this.cursorConfigs.x.current = mouse.x;
        this.cursorConfigs.y.current = mouse.y;
        for (const key in this.cursorConfigs) {
            this.cursorConfigs[key].previous = lerp(
                this.cursorConfigs[key].previous,
                this.cursorConfigs[key].current,
                this.cursorConfigs[key].amt
            );
        }
        this.Cursor.style.transform = `translateX(${this.cursorConfigs.x.previous }px) translateY(${this.cursorConfigs.y.previous}px)`;
        requestAnimationFrame(() => this.render());
    }
}
